package com.example.e_commerce.ui.fragments;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.lifecycle.LifecycleOwner;
import androidx.lifecycle.LiveData;
import androidx.lifecycle.Observer;
import androidx.lifecycle.ViewModelProvider;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.ItemTouchHelper;
import androidx.recyclerview.widget.RecyclerView;
import androidx.viewpager.widget.ViewPager;

import android.os.Handler;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import com.example.e_commerce.R;
import com.example.e_commerce.adapter.HomeProductAdapter;
import com.example.e_commerce.adapter.SlidingImage_Adapter;
import com.example.e_commerce.data.Room.AppDatabase;
import com.example.e_commerce.data.Room.AppExecutors;
import com.example.e_commerce.data.model.ProductsDetails.ProductDatum;
import com.example.e_commerce.databinding.FragmentHomeBinding;
import com.example.e_commerce.ui.viewmodels.HomeProductsViewModel;
import com.viewpagerindicator.CirclePageIndicator;

import java.util.List;
import java.util.Timer;
import java.util.TimerTask;


public class HomeFragment extends Fragment {
    private static ViewPager mPager;
    private CirclePageIndicator indicator;
    private static int currentPage = 0;
    private static int NUM_PAGES = 0;
    private FragmentHomeBinding binding;
    private HomeProductAdapter homeProductAdapter;
    private HomeProductsViewModel homeProductsViewModel;


    private String[] urls = new String[]{"https://skfashionsl.com/wp-content/uploads/2019/05/334BAFFB00000578-0-image-m-31_1461539901243.jpg",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTbhCvpDwUUyH6co4KWs6FUStkB66g0YGSfgw&usqp=CAU"};

    public HomeFragment() {
        // Required empty public constructor
    }


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {


        binding = FragmentHomeBinding.inflate(inflater, container, false);
        View view = binding.getRoot();
        initSlider(view);
        InitView();
        homeProductsViewModel = new ViewModelProvider(this).get(HomeProductsViewModel.class);
        homeProductsViewModel.getHomeProductsList();
        homeProductsViewModel.GetDatumList().observe((LifecycleOwner) getContext(), new Observer<List<ProductDatum>>() {
            @Override
            public void onChanged(List<ProductDatum> productData) {
                binding.shimmerViewContainer.setVisibility(View.GONE);
                homeProductAdapter.setHomeProductList(productData);
            }
        });

        return view;
    }

    private void InitView() {
        binding.shimmerViewContainer.startShimmer();
        homeProductAdapter = new HomeProductAdapter(getContext());
        GridLayoutManager layoutManager = new GridLayoutManager(getContext(), 2);
        binding.RecHomeProduct.setLayoutManager(layoutManager);
        binding.RecHomeProduct.setHasFixedSize(true);
        binding.RecHomeProduct.setAdapter(homeProductAdapter);

    }

    private void initSlider(View view) {

        mPager = view.findViewById(R.id.pager);
        indicator = view.findViewById(R.id.indicator);
        mPager.setAdapter(new SlidingImage_Adapter(getContext(), urls));


        indicator.setViewPager(mPager);

        final float density = getResources().getDisplayMetrics().density;

//Set circle indicator radius
        indicator.setRadius(5 * density);

        NUM_PAGES = urls.length;

        // Auto start of viewpager
        final Handler handler = new Handler();
        final Runnable Update = new Runnable() {
            public void run() {
                if (currentPage == NUM_PAGES) {
                    currentPage = 0;
                }
                mPager.setCurrentItem(currentPage++, true);
            }
        };
        Timer swipeTimer = new Timer();
        swipeTimer.schedule(new TimerTask() {
            @Override
            public void run() {
                handler.post(Update);
            }
        }, 5000, 5000);

        // Pager listener over indicator
        indicator.setOnPageChangeListener(new ViewPager.OnPageChangeListener() {

            @Override
            public void onPageSelected(int position) {
                currentPage = position;

            }

            @Override
            public void onPageScrolled(int pos, float arg1, int arg2) {

            }

            @Override
            public void onPageScrollStateChanged(int pos) {

            }
        });

    }
}